'use strict';

const path = require('path');

type ProjectInfo = {
  name: string;
  isWorkspace: boolean;
}

function findXcodeProject(files: Array<string>): ?ProjectInfo {
  const sortedFiles = files.sort();
  for (let i = sortedFiles.length - 1; i >= 0; i--) {
    const fileName = files[i];
    const ext = path.extname(fileName);

    if (ext === '.xcworkspace') {
