/**
                                     * Starts iOS device syslog tail
                                     */
function logIOS() {
  return new Promise((resolve, reject) => {
    _logIOS(resolve, reject);
  });
}

function _logIOS() {
  let rawDevices;

  try {
    rawDevices = child_process.execFileSync(
    'xcrun', ['simctl', 'list', 'devices', '--json'], { encoding: 'utf8' });

  } catch (e) {
    console.log(chalk.red(
