const express = require('express');
const mongoose = require('mongoose');
const Click = require('../models/click');
const Account = require('../models/account');
const router = express.Router();


router.get('/start', function (req, res) {
    let id = req.headers;
    let ip = req.header('x-forwarded-for') || req.connection.remoteAddress;
    res.send({ user : id, ip: ip });
});


router.post('/setupUser', function (req, res) {
  let { userId, version } = req.body;

  Account.create({ userId, version: version, userscreensize: 0 }, function(err, account) {
        if (err) {
            return err;
        }

        res.status(200);
    });
});

router.post('/pushUp', function (req, res) {
  let { userId, userIP, userscreensize, scrollPosition, timestamp } = req.body;
  Account.findOne({"userId": userId},function(err , account) {
    if (err) {
      console.log(err);
    }

    //userIP:
    let found = false;
    account.userIP.forEach((ip) => {
      if (ip === userIP) {
        found = true;
      }
    });
    if (found === false && userIP !== null) {
      account.userIP.push(userIP);
    }

    //userscreensize
    if (account.userscreensize === 0) {
      account.userscreensize = userscreensize;
    }

    //set new scrollPosition and timestamp:
    account.history.push({
      scrollPosition,
      timestamp
    });

    account.save(function (err) {
      if (err) {
        console.log(err);
      }
    });
  });
  res.status(201);
});



module.exports = router;

