import { spawnSync } from 'child_process';
import { writeFileSync } from 'fs';

let newFolder = spawnSync('mkdir', [ 'output' ]);
let cd = spawnSync('cd', [ 'output' ]);

let installs = `install --save-dev babel-cli babel-preset-es2015`;
installs = installs.split(' ');
let createFiles = `package.json index.js .babelrc .env .gitignore README.md`;
createFiles = createFiles.split(' ');

console.log('Creating files...');
let createFile = spawnSync( 'touch', createFiles, {cwd: './output'} );
if (createFile.stderr) {
  console.log(createFile.stderr.toString());
}

//Write to files:


//File contents:
const pkgJSON = {
  "name": "",
  "version": "0.0.0",
  "description": "",
  "main": "index.js",
  "scripts": {
    "test": "echo \"Error: no test specified\" && exit 1",
    "start": "babel-node index.js"
  },
  "author": "",
  "repository": {
    "type": "git",
    "url": ""
  },
  "license": "ISC"
};

const env = "";

const babelrc = {
  "presets": ["es2015"]
};

const gitignore = `
lib-cov
*.seed
*.log
*.csv
*.dat
*.out
*.pid
*.gz

.env
pids
logs
results

node_modules
npm-debug.log
`;

const README = `
# NAME_GOES_HERE

### BASIC_INFO_OF_PROJECT_GOES_HERE

INFORMATION_GOES_HERE

## Install

\`\`\`
npm install NAME_GOES_HERE
\`\`\`

## Usage
\`\`\`
EXAMPLE_USAGE_GOES_HERE

\`\`\`
`

jsonfile.writeFileSync('./output/package.json', pkgJSON);
writeFileSync('./output/.env', env);
jsonfile.writeFileSync('./output/.babelrc', babelrc);
writeFileSync('./output/.gitignore', gitignore);
jsonfile.writeFileSync('./output/package.json', pkgJSON);
writeFileSync('./output/README.md', README);



console.log('Installing Modules...');
let install = spawnSync( 'npm', installs, {cwd: './output'} );
if (install.stderr) {
  console.log(install.stderr.toString());
}

